template.Remote_Wakeup:
void Remote_Wakeup(void)
{
    /* remote wakeup */
    SetPower(GetPower() | 0x4);
    Delay_ms(10);
    SetPower(GetPower() & (~0x4));

    /* No Resume interrupt will be generated when the software initiates a remote wakeup */
    USBD_DCD_INT_fops->Resume(&USB_Device_dev);
}
